SPDataList.common:CreateProcedureSQL:
create procedure dbo.USR_USP_DATALIST_CONSTITUENT_ACTIVECHILD_CORRESPONDINGSPONSORSHIPS(@CONTEXTID uniqueidentifier) as set nocount on;
select financialsponsor.ID as FINANCIALSPONSORID, correspondent.ID as CORRESPONDENTID, sp.ID as SPONSORSHIPID, rev.ID as SPONSORSHIPRECURRINGGIFTID, sp.SPONSORSHIPOPPORTUNITYID, spc.LOOKUPID as COMMITMENTLOOKUPID, coalesce (slocation.LOOKUPID,'') as PROJECTLOOKUPID, so.LOOKUPID as CHILDLOOKUPID, so_child.NAME as CHILDNAME, statusCode.DESCRIPTION as SPONSORSHIPSTATUS, sp.STARTDATE from dbo.SPONSORSHIP sp inner join dbo.SPONSORSHIPCOMMITMENT spc on sp.SPONSORSHIPCOMMITMENTID = spc.ID inner join dbo.REVENUESPLIT revsplit on sp.REVENUESPLITID = revsplit.ID inner join dbo.REVENUE rev on revsplit.REVENUEID = rev.ID left join dbo.USR_FINANCIALTRANSACTION_EXTENSION ft_ext on rev.ID = ft_ext.ID inner join dbo.REVENUESCHEDULE revschedule on revschedule.ID = rev.ID --REVENUE.ID inner join dbo.CONSTITUENT financialsponsor on rev.CONSTITUENTID = financialsponsor.ID inner join dbo.CONSTITUENT correspondent on sp.CONSTITUENTID = correspondent.ID inner join dbo.SPONSORSHIPOPPORTUNITY so on so.ID = sp.SPONSORSHIPOPPORTUNITYID inner join dbo.SPONSORSHIPOPPORTUNITYCHILD so_child on so_child.ID = sp.SPONSORSHIPOPPORTUNITYID inner join dbo.USR_CHILDEXTENSION so_child_ext on so_child.ID = so_child_ext.SPONSORSHIPOPPORTUNITYCHILDID left join dbo.USR_SPONSORSHIPEXTENSION se on se.ID = sp.ID left join dbo.USR_CURRENTOPPORTUNITYSPONSORSHIPSTATUSCODE statusCode on statusCode.ID = se.CURRENTOPPORTUNITYSPONSORSHIPSTATUSCODEID and statusCode.DESCRIPTION in ('Sponsored', 'Prospect Sponsored', 'Unpaid Sponsored') inner join dbo.SPONSORSHIPLOCATION slocation on so.SPONSORSHIPLOCATIONID = slocation.ID inner join dbo.REVENUEPAYMENTMETHOD rev_paymethod on rev_paymethod.REVENUEID = revsplit.REVENUEID
where (sp.STATUSCODE = 1 and (sp.CONSTITUENTID = @CONTEXTID and rev.CONSTITUENTID <> @CONTEXTID))
