SaveImplementation.common:CreateProcedureSQL:
create procedure dbo.USR_USP_DATAFORMTEMPLATE_ADD_EMAIL_LAST_OPEN_CONSTITUENTS_BATCH ( @ID uniqueidentifier = null output, @BATCHID uniqueidentifier, @CHANGEAGENTID uniqueidentifier = null, @SEQUENCE int = null, @SPONSORID nvarchar(10) = '', @EMAILADDRESS nvarchar(250) = '' --@ISPROCESSED bit = 0, --@PROCESSDATE datetime = null ) as
set nocount on;
if @ID is null set @ID = newid()
if @CHANGEAGENTID is null exec dbo.USP_CHANGEAGENT_GETORCREATECHANGEAGENT @CHANGEAGENTID output
declare @CURRENTDATE datetime set @CURRENTDATE = getdate()
begin try -- Get the batch number of this batch & update the row: declare @BATCHNUMBER nvarchar(100) select @BATCHNUMBER = BATCHNUMBER from BATCH where ID = @BATCHID;
if @BATCHNUMBER is null raiserror ('@BATCHNUMBER is Null!!',13,1);
-- handle inserting the data insert into dbo.USR_EMAIL_LAST_OPEN_DATE_CONSTITUENTS_BATCH ([ID] ,BATCHID ,SEQUENCE ,[SPONSORID] ,[EMAILADDRESS] ,BATCHNUMBER --,[ISPROCESSED] --,[PROCESSDATE] ,[ADDEDBYID] ,[CHANGEDBYID] ,[DATEADDED] ,[DATECHANGED]) values (@ID ,@BATCHID ,@SEQUENCE ,@SPONSORID ,@EMAILADDRESS ,@BATCHNUMBER --,@ISPROCESSED --,@PROCESSDATE ,@CHANGEAGENTID ,@CHANGEAGENTID ,@CURRENTDATE ,@CURRENTDATE )
end try
begin catch exec dbo.USP_RAISE_ERROR return 1 end catch
return 0
