LoadImplementation.common:CreateProcedureSQL:
				
create procedure dbo.USR_USP_DATAFORMTEMPLATE_EDITLOAD_EMAIL_LAST_OPEN_CONSTITUENTS_BATCH(
    @ID uniqueidentifier,
	@SEQUENCE int = null output,
    @DATALOADED bit = 0 output,
    @TSLONG bigint = 0 output,
	@SPONSORID nvarchar(10) = null output,
	@EMAILADDRESS nvarchar(250) = null output
	--@ISPROCESSED bit = null output,
	--@PROCESSDATE datetime = null output
)
as

	set nocount on;

	-- be sure to set these, in case the select returns no rows
	set @DATALOADED = 0
	set @TSLONG = 0

	-- populate the output parameters, which correspond to fields on the form.  Note that
	-- we set @DATALOADED = 1 to indicate that the load was successful.  Otherwise, the system
	-- will display a "no data loaded" message.  Also note that we fetch the TSLONG so that concurrency
	-- can be considered.
	select
		@DATALOADED = 1,
		@TSLONG = TSLONG,
		@SEQUENCE = SEQUENCE,
		@SPONSORID = SPONSORID,
		@EMAILADDRESS = EMAILADDRESS
		--@ISPROCESSED = ISPROCESSED,
		--@PROCESSDATE = PROCESSDATE
	from dbo.USR_EMAIL_LAST_OPEN_DATE_CONSTITUENTS_BATCH
	where ID = @ID

	return 0;

SaveImplementation.common:CreateProcedureSQL:
create procedure dbo.USR_USP_DATAFORMTEMPLATE_EDIT_EMAIL_LAST_OPEN_CONSTITUENTS_BATCH ( @ID uniqueidentifier, @SEQUENCE int, @CHANGEAGENTID uniqueidentifier = null, @SPONSORID nvarchar(10), @EMAILADDRESS nvarchar(250) --@ISPROCESSED bit, --@PROCESSDATE datetime ) as
set nocount on;
if @CHANGEAGENTID is null exec dbo.USP_CHANGEAGENT_GETORCREATECHANGEAGENT @CHANGEAGENTID output
declare @CURRENTDATE datetime set @CURRENTDATE = getdate()
begin try -- handle updating the data update dbo.USR_EMAIL_LAST_OPEN_DATE_CONSTITUENTS_BATCH set SPONSORID = @SPONSORID, SEQUENCE = @SEQUENCE, EMAILADDRESS = @EMAILADDRESS, --ISPROCESSED = @ISPROCESSED, --PROCESSDATE = @PROCESSDATE, CHANGEDBYID = @CHANGEAGENTID, DATECHANGED = @CURRENTDATE where ID = @ID end try begin catch exec dbo.USP_RAISE_ERROR return 1 end catch
return 0;
