SPDataForm.common:CreateProcedureSQL:
create procedure dbo.USR_USP_DATAFORMTEMPLATE_VIEW_CONSTITUENTOCM_PAGEEXPRESSION ( @ID uniqueidentifier, @DATALOADED bit = 0 output, @HASCHURCHMEMBERS bit = null output, @HASADDITIONALGIVING bit = null output ) as set nocount on;
-- be sure to set this, in case the select returns no rows set @DATALOADED = 0;
-- populate the output parameters, which correspond to fields on the form. Note that -- we set @DATALOADED = 1 to indicate that the load was successful. Otherwise, the system -- will display a "no data loaded" message. select @DATALOADED = 1, @HASCHURCHMEMBERS = case when exists (select r.ID from dbo.RELATIONSHIP r inner join dbo.RELATIONSHIPTYPECODE rc on r.RELATIONSHIPTYPECODEID = rc.ID where CONSTITUENT.ISORGANIZATION = 1 and rc.DESCRIPTION = 'Church Member' and r.RECIPROCALCONSTITUENTID = CONSTITUENT.ID and r.ENDDATE is null) then 1 else 0 end, @HASADDITIONALGIVING = case when (exists (select [V_QUERY_REVENUE].ID from [V_QUERY_REVENUE] as [V_QUERY_REVENUE] inner join [V_QUERY_REVENUESPLIT] as [V_QUERY_REVENUE\Revenue Splits] on [V_QUERY_REVENUE].[ID] = [V_QUERY_REVENUE\Revenue Splits].[REVENUEID] left outer join [V_QUERY_DESIGNATION] as [V_QUERY_REVENUE\Revenue Splits\Designation] on [V_QUERY_REVENUE\Revenue Splits].[DESIGNATIONID] = [V_QUERY_REVENUE\Revenue Splits\Designation].[ID] left outer join [V_QUERY_REVENUE] as [V_QUERY_REVENUE\Revenue Splits\Revenue Details] on [V_QUERY_REVENUE\Revenue Splits].[REVENUEID] = [V_QUERY_REVENUE\Revenue Splits\Revenue Details].[ID] left outer join [V_QUERY_REVENUELETTER] as [V_QUERY_REVENUE\Revenue Splits\Revenue Details\Revenue Letters] on [V_QUERY_REVENUE\Revenue Splits\Revenue Details].[ID] = [V_QUERY_REVENUE\Revenue Splits\Revenue Details\Revenue Letters].[REVENUEID] left outer join [V_QUERY_CONSTITUENT] as [V_QUERY_REVENUE\Constituent] on [V_QUERY_REVENUE].[CONSTITUENTID] = [V_QUERY_REVENUE\Constituent].[ID] where [V_QUERY_REVENUE].[TRANSACTIONTYPE] = N'Payment' and [V_QUERY_REVENUE\Revenue Splits].[APPLICATION] = N'Donation' and [V_QUERY_REVENUE\Revenue Splits].[TYPE] = N'Gift' and [V_QUERY_REVENUE\Constituent].ID = CONSTITUENT.ID ) or exists (select ft.ID from dbo.FINANCIALTRANSACTION ft inner join dbo.REVENUESPLIT revsplit on revsplit.REVENUEID = ft.ID inner join dbo.CONSTITUENT c on ft.CONSTITUENTID = c.ID where ft.TYPECODE = 2 -- Recurring gift and not exists (select ID from dbo.SPONSORSHIP sp where sp.REVENUESPLITID = revsplit.ID) and c.ID = CONSTITUENT.ID ))
then 1 else 0 end
from dbo.CONSTITUENT
where ID = @ID
return 0;
